Row.code:
// Populate properties for the house the first time the service runs only.
if (Things["MyHouse"].houseID == "") {
	Things["MyHouse"].houseID = "12345";
	Things["MyHouse"].address = "140 Kendrick Street";
	Things["MyHouse"].city = "Needham";
	Things["MyHouse"].state = "MA";
	Things["MyHouse"].zip = "02494";
	Things["MyHouse"].house_lat_long = "42.296375,-71.214556,0";
	Things["MyHouse"].lightID = "11111";
	Things["MyHouse"].thermostatID = "99999";
	Things["MyHouse"].temperature = 70;
	Things["Foundation_Quickstart_Services"].flag = "true";
}

// Light Properties //

// Every 30 seconds change the light status to ON (100 watts) or OFF (0 watts)
if (Things["MyHouse"].watts == "0") {
	Things["MyHouse"].watts = 100;
} else if (Things["MyHouse"].watts == "100") {
	Things["MyHouse"].watts = 0;
} else {
	Things["MyHouse"].watts = 0;
}

// Thermostat Properties //

// Temporary fix. When the temperature is supposed to be set to 69.6 is set to 69.60000000000000001.
var temp = Things["MyHouse"].temperature;
temp = Things["MyHouse"].temperature.toFixed(1);
temp = parseFloat(temp);

// Increase temperature a small amount until it reachs 70. Once it exceeds 70, turn AC ON, and then decrement at a faster pace back to 68.
if(temp < 70 && temp > 68 && me.flag == true) {
	Things["MyHouse"].temperature = temp + 0.2;
    //logger.error("Increasing temperature: " + Things["MyHouse"].temperature);
} else if (temp < 70 && temp > 68 && me.flag == false)  {
    Things["MyHouse"].temperature = temp - 0.5;
    //logger.error("Decreasing temperature: " + Things["MyHouse"].temperature);
} else if (temp == 70) {
 	Things["MyHouse"].temperature = temp - 0.5;
    me.flag = false;
    //logger.error("Temperature reached 70, decreasing: " + Things["MyHouse"].temperature);
} else if (temp == 68) {
 	Things["MyHouse"].temperature = temp + 0.2;
    me.flag = true;
    //logger.error("Temperature reached 68, increasing: " + Things["MyHouse"].temperature);
}
